import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import '../styles/Services.css';

const Services = () => {
  const services = [
    {
      title: 'Social Media Management',
      description: 'We handle your brand\'s social presence like prosâ€”planning, posting, and engaging daily.',
      icon: 'ðŸ“±',
      visual: 'Command center dashboard with ninja-themed social media tools.'
    },
    {
      title: 'Content Creation',
      description: 'Scroll-stopping posts, reels, and visuals designed to go viral.',
      icon: 'ðŸŽ¨',
      visual: 'Designer\'s desk with ninja tools (camera, stylus, storyboard)'
    },
    {
      title: 'Paid Advertising',
      description: 'Ad campaigns with razor-sharp targeting to bring real ROI.',
      icon: 'ðŸ’°',
      visual: 'Target icon + upward graphs + currency symbols'
    },
    {
      title: 'Influencer Marketing',
      description: 'Collaborate with creators who influence your audience\'s decisions.',
      icon: 'ðŸŒŸ',
      visual: 'Ninja passing a scroll (your brand) to an influencer on stage'
    },
    {
      title: 'Analytics & Reporting',
      description: 'Track growth with clear, real-time data reports and insights.',
      icon: 'ðŸ“Š',
      visual: 'Dark-themed dashboard with glowing charts, ninja data visor'
    }
  ];

  return (
    <section id="services" className="services-section">
      <Container>
        <h2 className="text-center mb-5">What We Do Best</h2>
        <Row>
          {services.map((service, index) => (
            <Col key={index} lg={4} md={6} className="mb-4">
              <Card className="service-card h-100">
                <Card.Body>
                  <div className="service-icon mb-3">
                    <span className="icon">{service.icon}</span>
                  </div>
                  <Card.Title>{service.title}</Card.Title>
                  <Card.Text>{service.description}</Card.Text>
                  <div className="service-visual mt-3">
                    <small className="text-muted">{service.visual}</small>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default Services;
