import React from 'react';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const Home = () => {
  return (
    <div className="home-page">
      {/* Hero Section */}
      <section className="hero-section min-vh-100 d-flex align-items-center">
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-6">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <h1 className="display-4 fw-bold mb-4">
                  Dominate Social Media Like a Ninja
                </h1>
                <p className="lead mb-4">
                  At ConnectNinja, we craft sharp, smart, and strategic social media campaigns 
                  that help your brand cut through the noise and capture attention.
                </p>
                <div className="d-flex gap-3">
                  <Link to="/contact" className="btn btn-ninja">
                    Get a Free Strategy Session
                  </Link>
                  <Link to="/contact" className="btn btn-outline-dark">
                    Let's Connect
                  </Link>
                </div>
              </motion.div>
            </div>
            <div className="col-lg-6">
              <motion.div
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: 0.2 }}
                className="text-center"
              >
                <img 
                  src="/hero-image.svg" 
                  alt="Ninja Social Media" 
                  className="img-fluid"
                />
              </motion.div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Home;
