import React from 'react';
import '../styles/CoFounders.css';

const coFounders = [
  {
    name: 'Alex Kimura',
    title: 'CEO & Growth Ninja',
    img: 'https://randomuser.me/api/portraits/men/32.jpg',
    desc: 'Visionary leader with a passion for digital growth and creative strategy.'
  },
  {
    name: 'Priya Sharma',
    title: 'COO & Brand Architect',
    img: 'https://randomuser.me/api/portraits/women/44.jpg',
    desc: 'Expert in brand building and operational excellence, making ideas reality.'
  },
  {
    name: 'Jordan Lee',
    title: 'CTO & Tech Sensei',
    img: 'https://randomuser.me/api/portraits/men/65.jpg',
    desc: 'Tech innovator focused on scalable solutions and seamless user experience.'
  }
];

const CoFounders = () => (
  <section className="cofounders-section">
    <h2 className="cofounders-title">Meet Our Co-Founders</h2>
    <div className="cofounders-cards">
      {coFounders.map((founder, idx) => (
        <div className="cofounder-card" key={idx}>
          <div className="cofounder-img-wrap">
            <img src={founder.img} alt={founder.name} className="cofounder-img" />
          </div>
          <h3 className="cofounder-name">{founder.name}</h3>
          <div className="cofounder-title">{founder.title}</div>
          <p className="cofounder-desc">{founder.desc}</p>
        </div>
      ))}
    </div>
  </section>
);

export default CoFounders;
