import React from 'react';
import { motion } from 'framer-motion';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import teamIllustration from '../assets/team-illustration.svg';

const About = () => {
  const coFounders = [
    {
      name: 'Yatharth Deheriya',
      role: 'Co-Founder & CEO',
      image: 'https://via.placeholder.com/400x500',
      linkedin: 'https://linkedin.com/in/yatharthdeheriya',
      bio: 'Leads brand strategy and business growth.'
    },
    {
      name: 'Rajul Dew',
      role: 'Co-Founder',
      image: 'https://via.placeholder.com/400x500',
      linkedin: 'https://linkedin.com/in/rajuldew',
      bio: 'Heads content direction and creative campaigns.'
    }
  ];

  const coreTeam = [
    {
      name: 'John Smith',
      role: 'Web Developer',
      image: 'https://via.placeholder.com/200x200',
      linkedin: 'https://linkedin.com/in/johnsmith'
    },
    {
      name: 'Sarah Johnson',
      role: 'Social Media Manager',
      image: 'https://via.placeholder.com/200x200',
      linkedin: 'https://linkedin.com/in/sarahjohnson'
    },
    {
      name: 'Mike Brown',
      role: 'Video Editor',
      image: 'https://via.placeholder.com/200x200',
      linkedin: 'https://linkedin.com/in/mikebrown'
    },
    {
      name: 'Emily Davis',
      role: 'Graphic Designer',
      image: 'https://via.placeholder.com/200x200',
      linkedin: 'https://linkedin.com/in/emilydavis'
    }
  ];

  return (
    <div className="about-page">
      <section className="hero-section">
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-6">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <h1 className="display-4 fw-bold mb-4">Meet the Ninjas Behind Your Success</h1>
                <p className="lead mb-4">
                  We're a team of passionate social media experts dedicated to helping businesses grow their online presence.
                  Our diverse backgrounds and expertise allow us to create unique strategies that deliver real results.
                </p>
              </motion.div>
            </div>
            <div className="col-lg-6">
              <motion.img
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.5, delay: 0.2 }}
                src={teamIllustration}
                alt="Team Illustration"
                className="img-fluid"
              />
            </div>
          </div>
        </div>
      </section>

      {/* Co-Founders Section */}
      <section className="co-founders-section py-5">
        <div className="container">
          <h2 className="section-title text-center mb-5">Our Co-Founders</h2>
          <div className="row">
            {coFounders.map((founder, index) => (
              <motion.div
                key={index}
                className="col-md-6 mb-4"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5, delay: index * 0.2 }}
              >
                <div className="founder-card">
                  <div className="founder-image">
                    <img src={founder.image} alt={founder.name} className="img-fluid" />
                  </div>
                  <div className="founder-content">
                    <h3 className="h4 mb-2">{founder.name}</h3>
                    <p className="role mb-3">{founder.role}</p>
                    <p className="bio mb-4">{founder.bio}</p>
                    <a
                      href={founder.linkedin}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="btn btn-linkedin"
                    >
                      <i className="fab fa-linkedin me-2"></i>Connect on LinkedIn
                    </a>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Core Team Section */}
      <section className="core-team-section py-5 bg-light">
        <div className="container">
          <h2 className="section-title text-center mb-5">Our Core Team</h2>
          <Swiper
            modules={[Autoplay, Pagination]}
            spaceBetween={30}
            slidesPerView={1}
            pagination={{ clickable: true }}
            autoplay={{ delay: 3000 }}
            breakpoints={{
              640: { slidesPerView: 2 },
              768: { slidesPerView: 3 },
              1024: { slidesPerView: 4 }
            }}
            className="team-slider"
          >
            {coreTeam.map((member, index) => (
              <SwiperSlide key={index}>
                <motion.div
                  className="team-member-card"
                  whileHover={{ scale: 1.05 }}
                  transition={{ duration: 0.3 }}
                >
                  <a
                    href={member.linkedin}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-decoration-none"
                  >
                    <div className="member-image">
                      <img src={member.image} alt={member.name} className="img-fluid" />
                    </div>
                    <div className="member-info">
                      <h4 className="h5 mb-1">{member.name}</h4>
                      <p className="role mb-0">{member.role}</p>
                    </div>
                  </a>
                </motion.div>
              </SwiperSlide>
            ))}
          </Swiper>
        </div>
      </section>
    </div>
  );
};

export default About;
