import React, { useRef, useEffect } from 'react';
import { Container, Row, Col, Button, Form } from 'react-bootstrap';
import '../styles/Hero.css';

const socialPlatforms = [
  { name: 'YouTube', icon: '📺' },
  { name: 'Bluesky', icon: '🦋' },
  { name: 'Facebook', icon: '📘' },
  { name: 'LinkedIn', icon: '💼' },
  { name: 'Instagram', icon: '📸' },
  { name: 'Twitter', icon: '🐦' },
];

const Hero = () => {
  const scrollRef = useRef(null);

  useEffect(() => {
    const interval = setInterval(() => {
      if (scrollRef.current) {
        scrollRef.current.scrollTop += 2;
        if (
          scrollRef.current.scrollTop >=
          scrollRef.current.scrollHeight - scrollRef.current.clientHeight
        ) {
          scrollRef.current.scrollTop = 0;
        }
      }
    }, 30);
    return () => clearInterval(interval);
  }, []);

  return (
    <section className="hero-section-light">
      <Container fluid="lg">
        <Row className="align-items-center hero-row-wide">
          <Col xl={7} lg={7} md={12} className="hero-main-col text-lg-start text-center">
            <h1 className="hero-headline hero-headline-gap">Turn Clicks into Customers with ConnectNinja</h1>
            <div className="hero-email-form mb-4">
              <Form className="d-flex flex-column flex-md-row align-items-center align-items-md-stretch justify-content-md-start justify-content-center">
                <Form.Control
                  type="email"
                  placeholder="Enter your email..."
                  className="hero-email-input"
                />
                <Button type="submit" className="get-started-btn ms-md-3 mt-3 mt-md-0">
                  Get started now →
                </Button>
              </Form>
              <div className="hero-email-note text-md-start text-center">
                By entering your email, you agree to receive emails from ConnectNinja.
              </div>
            </div>
            <p className="hero-subheadline mb-4 text-md-start text-center">
              Struggling to get real results from social media? At ConnectNinja, we blend creativity with
              proven strategy to help your brand stand out, attract the right audience, and turn casual
              scrollers into loyal customers. Whether you're just starting out or ready to scale, we'll craft
              campaigns that actually work without the fluff.
            </p>
            <Button
              variant="danger"
              size="lg"
              className="cta-button mt-2 text-md-start"
              onClick={() => window.location.href = '#contact'}
            >
              Book a Free Strategy Call
            </Button>
          </Col>
          <Col xl={5} lg={5} className="d-none d-lg-flex align-items-center justify-content-center">
            <div className="social-scroll-container" ref={scrollRef}>
              {socialPlatforms.concat(socialPlatforms).map((platform, idx) => (
                <div className="social-scroll-item" key={idx}>
                  <span className="social-icon">{platform.icon}</span>
                  <span className="social-name">{platform.name}</span>
                </div>
              ))}
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default Hero;
