import React from 'react';
import { Container, Row, Col, Card } from 'react-bootstrap';
import { FaLinkedin } from 'react-icons/fa';
import '../styles/About.css';

const About = () => {
  const coFounders = [
    {
      name: 'Yatharth Deheriya',
      role: 'Co-Founder & CEO',
      bio: 'Leads brand strategy and business growth.',
      linkedin: '#', // Add LinkedIn URL
      image: '/path-to-image.jpg' // Add image path
    },
    {
      name: 'Rajul Dew',
      role: 'Co-Founder',
      bio: 'Heads content direction and creative campaigns.',
      linkedin: '#', // Add LinkedIn URL
      image: '/path-to-image.jpg' // Add image path
    }
  ];

  const teamMembers = [
    { name: 'Web Developer', role: 'Web Developer', linkedin: '#', image: '/path-to-image.jpg' },
    { name: 'Social Media Manager', role: 'Social Media Manager', linkedin: '#', image: '/path-to-image.jpg' },
    { name: 'Video Editor', role: 'Video Editor', linkedin: '#', image: '/path-to-image.jpg' },
    { name: 'Graphic Designer', role: 'Graphic Designer', linkedin: '#', image: '/path-to-image.jpg' }
  ];

  return (
    <section id="about" className="about-section">
      <Container>
        <h2 className="text-center mb-5">Meet the Ninja Crew</h2>
        <p className="text-center mb-5">
          ConnectNinja is powered by a passionate team of marketing minds, designers, and developers 
          from IITs and NITs, each playing a key role in helping brands grow fast and smart.
        </p>

        {/* Co-Founders Section */}
        <div className="co-founders mb-5">
          {coFounders.map((founder, index) => (
            <Card key={index} className="founder-card mb-4">
              <Card.Body>
                <Row>
                  <Col md={4}>
                    <div className="founder-image">
                      <img src={founder.image} alt={founder.name} className="img-fluid rounded" />
                    </div>
                  </Col>
                  <Col md={8}>
                    <h3>{founder.name}</h3>
                    <h4>{founder.role}</h4>
                    <p>{founder.bio}</p>
                    <a href={founder.linkedin} target="_blank" rel="noopener noreferrer" className="linkedin-btn">
                      <FaLinkedin /> LinkedIn
                    </a>
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          ))}
        </div>

        {/* Team Members Section */}
        <h3 className="text-center mb-4">Core Team</h3>
        <Row className="team-members">
          {teamMembers.map((member, index) => (
            <Col key={index} md={3} className="mb-4">
              <Card className="team-card h-100">
                <a href={member.linkedin} target="_blank" rel="noopener noreferrer">
                  <Card.Img variant="top" src={member.image} alt={member.name} />
                  <Card.Body>
                    <Card.Title>{member.name}</Card.Title>
                    <Card.Text>{member.role}</Card.Text>
                  </Card.Body>
                </a>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </section>
  );
};

export default About;
