import React from 'react';
import { motion } from 'framer-motion';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Autoplay, Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import 'swiper/css/autoplay';
import Header from '../components/Header';
import heroImage from '../assets/hero-image.svg';
import teamIllustration from '../assets/team-illustration.svg';

// Import team images
import yatharthImage from '../assets/team/yatharth.svg';
import rajulImage from '../assets/team/rajul.svg';
import johnImage from '../assets/team/john.svg';
import sarahImage from '../assets/team/sarah.svg';
import mikeImage from '../assets/team/mike.svg';
import emilyImage from '../assets/team/emily.svg';

const Home = () => {
  const coFounders = [
    {
      name: 'Yatharth Deheriya',
      role: 'Co-Founder & CEO',
      image: yatharthImage,
      linkedin: 'https://linkedin.com/in/yatharthdeheriya',
      bio: 'Leads brand strategy and business growth.'
    },
    {
      name: 'Rajul Dew',
      role: 'Co-Founder',
      image: rajulImage,
      linkedin: 'https://linkedin.com/in/rajuldew',
      bio: 'Heads content direction and creative campaigns.'
    }
  ];

  const coreTeam = [
    {
      name: 'John Smith',
      role: 'Web Developer',
      image: johnImage,
      linkedin: 'https://linkedin.com/in/johnsmith'
    },
    {
      name: 'Sarah Johnson',
      role: 'Social Media Manager',
      image: sarahImage,
      linkedin: 'https://linkedin.com/in/sarahjohnson'
    },
    {
      name: 'Mike Brown',
      role: 'Video Editor',
      image: mikeImage,
      linkedin: 'https://linkedin.com/in/mikebrown'
    },
    {
      name: 'Emily Davis',
      role: 'Graphic Designer',
      image: emilyImage,
      linkedin: 'https://linkedin.com/in/emilydavis'
    }
  ];

  return (
    <div className="home-page">
      <Header />

      {/* Hero Section */}
      <section className="hero-section">
        <div className="container">
          <div className="row align-items-center">
            <div className="col-lg-6">
              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.5 }}
              >
                <h1 className="display-4 fw-bold mb-5">Turn Clicks into Customers with ConnectNinja</h1>
                <p className="lead mb-5">
                  Transform your social media presence into a powerful lead generation machine. 
                  Our expert team creates engaging content that converts followers into loyal customers.
                </p>
